import React, { useState, useEffect } from "react";
import {
  Typography,
  Box,
  TextField,
  Button,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Paper,
  CircularProgress,
  OutlinedInput,
  Chip,
} from "@mui/material";
import SendIcon from "@mui/icons-material/Send";
import axios from "axios";

const LLMPage = () => {
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [prompt, setPrompt] = useState("");
  const [response, setResponse] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchUploadedFiles();
  }, []);

  const fetchUploadedFiles = async () => {
    try {
      const res = await axios.get("http://localhost:8000/api/files");
      setUploadedFiles(res.data.files);
    } catch (err) {
      console.error("Error fetching file list:", err);
    }
  };

  const handleSubmit = async () => {
    if (selectedFiles.length === 0 || !prompt) {
      alert("Please select at least one file and enter a prompt.");
      return;
    }

    setLoading(true);
    setResponse(null);

    try {
      const res = await axios.post("http://localhost:8000/api/query", {
        filenames: selectedFiles,
        prompt: prompt,
      });

      setResponse(res.data.response || "No response received.");
    } catch (err) {
      console.error("Query error:", err);
      setResponse("Error occurred while querying.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <Box p={4} display="flex" flexDirection="column" gap={3} maxWidth="700px">
      <Typography variant="h4">Ask Questions (LLM)</Typography>
      <Typography variant="body1">
        Use AI to ask natural language questions about your uploaded data.
      </Typography>

      {/* Multi-select file dropdown */}
      <FormControl fullWidth>
        <InputLabel id="multi-file-label">Select File(s)</InputLabel>
        <Select
          labelId="multi-file-label"
          multiple
          value={selectedFiles}
          onChange={(e) => setSelectedFiles(e.target.value)}
          input={<OutlinedInput label="Select File(s)" />}
          renderValue={(selected) => (
            <Box sx={{ display: "flex", flexWrap: "wrap", gap: 0.5 }}>
              {selected.map((value) => (
                <Chip key={value} label={value} />
              ))}
            </Box>
          )}
        >
          {uploadedFiles.map((file, idx) => (
            <MenuItem key={idx} value={file}>
              {file}
            </MenuItem>
          ))}
        </Select>
      </FormControl>

      {/* Prompt input */}
      <TextField
        label="Enter your question or prompt"
        multiline
        minRows={3}
        value={prompt}
        onChange={(e) => setPrompt(e.target.value)}
        fullWidth
      />

      {/* Submit button */}
      <Button
        variant="contained"
        endIcon={<SendIcon />}
        onClick={handleSubmit}
        disabled={loading}
        sx={{ alignSelf: "flex-start" }}
      >
        Submit Query
      </Button>

      {loading && <CircularProgress />}

      {response && (
        <Paper elevation={2} sx={{ padding: 2, backgroundColor: "#f9f9f9" }}>
          <Typography variant="h6">Response:</Typography>
          <Typography variant="body1" sx={{ mt: 1 }}>
            {response}
          </Typography>
        </Paper>
      )}
    </Box>
  );
};

export default LLMPage;
