import React, { useRef, useState, useEffect } from "react";
import {
  Typography,
  Box,
  Button,
  Select,
  MenuItem,
  InputLabel,
  FormControl,
  TextField,
  Paper,
  CircularProgress,
} from "@mui/material";
import UploadFileIcon from "@mui/icons-material/UploadFile";
import axios from "axios";

const UploadPage = () => {
  const fileInputRef = useRef(null);
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [selectedFile, setSelectedFile] = useState("");
  const [numRows, setNumRows] = useState("");
  const [previewData, setPreviewData] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchUploadedFiles();
  }, []);

  const fetchUploadedFiles = async () => {
    try {
      const response = await axios.get("http://localhost:8000/api/files");
      setUploadedFiles(response.data.files);
    } catch (err) {
      console.error("Error fetching file list:", err);
    }
  };

  const handleFileChange = async (event) => {
    const files = event.target.files;

    if (!files.length) return;

    const formData = new FormData();
    for (let file of files) {
      formData.append("files", file);
    }

    try {
      await axios.post("http://localhost:8000/api/upload", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
      await fetchUploadedFiles(); // Refresh file list after upload
    } catch (err) {
      console.error("Error uploading files:", err);
    }
  };

  const handlePreview = async () => {
    if (!selectedFile || !numRows) {
      alert("Please select a file and specify the number of rows.");
      return;
    }

    setLoading(true);
    setPreviewData(null);

    try {
      const response = await axios.get("http://localhost:8000/api/preview", {
        params: {
          filename: selectedFile,
          n: numRows,
        },
      });
      setPreviewData(response.data.preview);
    } catch (err) {
      console.error("Error fetching preview:", err);
      setPreviewData("Error loading data.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <Box p={4} display="flex" flexDirection="column" gap={3}>
      <Typography variant="h4">Upload Files</Typography>
      <Typography variant="body1">
        Upload one or more CSV or Excel files to start exploring with AI.
      </Typography>

      <div
        style={{ display: "flex", justifyContent: "center", marginTop: "16px" }}
      >
        <Button
          variant="contained"
          startIcon={<UploadFileIcon />}
          onClick={() => fileInputRef.current.click()}
          sx={{ maxWidth: "300px", mr: '16px' }}
        >
          Upload CSV/XLS Files
        </Button>
        {selectedFile && (
          <Button
            variant="contained"
            startIcon={<UploadFileIcon />}
            onClick={() => fileInputRef.current.click()}
            sx={{ maxWidth: "300px" }}
          >
            Query LLM
          </Button>
        )}
      </div>

      <input
        type="file"
        accept=".csv, application/vnd.ms-excel, application/vnd.openxmlformats-officedocument.spreadsheetml.sheet"
        multiple
        style={{ display: "none" }}
        ref={fileInputRef}
        onChange={handleFileChange}
      />

      {uploadedFiles.length > 0 && (
        <FormControl fullWidth>
          <InputLabel id="file-select-label">Select File</InputLabel>
          <Select
            labelId="file-select-label"
            value={selectedFile}
            label="Select File"
            onChange={(e) => setSelectedFile(e.target.value)}
          >
            {uploadedFiles.map((file, idx) => (
              <MenuItem key={idx} value={file}>
                {file}
              </MenuItem>
            ))}
          </Select>
        </FormControl>
      )}

      {selectedFile && (
        <TextField
          label="Number of rows to display (N)"
          type="number"
          value={numRows}
          onChange={(e) => setNumRows(e.target.value)}
          fullWidth
        />
      )}

      {selectedFile && numRows && (
        <Button variant="outlined" onClick={handlePreview}>
          Preview Top {numRows} Rows
        </Button>
      )}

      {loading && <CircularProgress />}

      {previewData && Array.isArray(previewData) && (
        <Paper elevation={2} sx={{ padding: 2, backgroundColor: "#f9f9f9" }}>
          <Typography variant="h6" gutterBottom>
            Top {numRows} Rows of {selectedFile}
          </Typography>

          <Box sx={{ overflowX: "auto" }}>
            <table style={{ width: "100%", borderCollapse: "collapse" }}>
              <thead>
                <tr>
                  {Object.keys(previewData[0]).map((col, index) => (
                    <th
                      key={index}
                      style={{
                        border: "1px solid #ddd",
                        padding: "8px",
                        backgroundColor: "#f0f0f0",
                        fontWeight: "bold",
                      }}
                    >
                      {col}
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {previewData.map((row, rowIndex) => (
                  <tr key={rowIndex}>
                    {Object.values(row).map((cell, cellIndex) => (
                      <td
                        key={cellIndex}
                        style={{ border: "1px solid #ddd", padding: "8px" }}
                      >
                        {cell !== null ? cell.toString() : ""}
                      </td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </Box>
        </Paper>
      )}
    </Box>
  );
};

export default UploadPage;
