import React, { useState } from "react";
import {
  Typography,
  Box,
  TextField,
  Button,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
} from "@mui/material";
import SendIcon from "@mui/icons-material/Send";

const LLMPage = () => {
  const [selectedFile, setSelectedFile] = useState("");
  const [prompt, setPrompt] = useState("");

  const handleSubmit = () => {
    if (!selectedFile || !prompt) {
      alert("Please select a file and enter a prompt.");
      return;
    }
    console.log("Query submitted:", { file: selectedFile, prompt });
    // Send to backend later
  };

  return (
    <Box p={4} display="flex" flexDirection="column" gap={3} maxWidth={600}>
      <Typography variant="h4">Ask Questions (LLM)</Typography>
      <Typography variant="body1">
        Use AI to ask questions about your uploaded CSV/XLS files.
      </Typography>

      {/* File Selector */}
      <FormControl fullWidth>
        <InputLabel id="file-select-label">Select File</InputLabel>
        <Select
          labelId="file-select-label"
          value={selectedFile}
          label="Select File"
          onChange={(e) => setSelectedFile(e.target.value)}
        >
          {/* Replace these with dynamic uploaded filenames later */}
          <MenuItem value="titanic.csv">titanic.csv</MenuItem>
          <MenuItem value="sales_data.xlsx">sales_data.xlsx</MenuItem>
        </Select>
      </FormControl>

      {/* Prompt Input */}
      <TextField
        label="Enter your question or prompt"
        multiline
        minRows={3}
        value={prompt}
        onChange={(e) => setPrompt(e.target.value)}
        fullWidth
        variant="outlined"
      />

      {/* Submit Button */}
      <Button
        variant="contained"
        endIcon={<SendIcon />}
        onClick={handleSubmit}
        sx={{ alignSelf: "flex-start" }}
      >
        Submit Query
      </Button>
    </Box>
  );
};

export default LLMPage;
